        jTextArea1.setRows(5);
        jTextArea1.setText("Otitis Media Akut adalah infeksi telinga tengah oleh bakteri atau virus. Otitis media akut bisa terjadi pada semua usia, tetapi paling sering ditemukan pada anak-anak terutama usia 3 bln - 3 thn.");
        jScrollPane1.setViewportView(jTextArea1);

